config.script:
lastYearCount = self.getChild("root").getChild("GuestNumberSlider").custom.lastSeason
thisYearCount = self.params.guestCount
if thisYearCount == '':
	if lastYearCount < 8:
		thisYearStart = 8
	elif lastYearCount < 12:
		thisYearStart = 12
	else:
		thisYearStart = lastYearCount + 1
	self.params.guestCount = thisYearStart
	self.params.startGuestCount = thisYearStart

if self.parent.custom.addBackupCoord:
	self.parent.custom.addBackupCoord = False
else:
	self.parent.custom.addBackupCoord = True

if self.parent.custom.addBackupCoord:
	self.parent.custom.addBackupCoord = False
else:
	self.parent.custom.addBackupCoord = True

if self.getSibling("PartnersTextField").props.text == '' or self.getSibling("PartnersTextField").props.text is None:
	return
if self.getSibling("PartnersList").props.text == '' or self.getSibling("PartnersList").props.text is None:
	self.getSibling("PartnersList").props.text = self.getSibling("PartnersTextField").props.text
else:
	self.getSibling("PartnersList").props.text += '; ' + self.getSibling("PartnersTextField").props.text

self.getSibling("PartnersTextField").props.text = ''
